import React, { useState, useEffect } from 'react';
import { userData } from './data'; // Assuming you have a data file
import { GoogleMap, LoadScript, DirectionsService, DirectionsRenderer } from '@react-google-maps/api';

const BusRouteList = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredRoute, setFilteredRoute] = useState(null);
  const [currentPosition, setCurrentPosition] = useState(null);
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [rating, setRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);
  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setCurrentPosition({
        lat: position.coords.latitude,
        lng: position.coords.longitude,
      });
    });
  }, []);

  // Generate random rating when the route is filtered
  useEffect(() => {
    if (filteredRoute) {
      const randomRating = Math.floor(Math.random() * 5) + 1; // Random rating between 1 and 5
      setRating(randomRating);
    }
  }, [filteredRoute]);

  const handleInputChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = (e) => {
    e.preventDefault();

    const result = userData.find((user) =>
      user.routename.toLowerCase().includes(searchTerm.toLowerCase())
    );

    setFilteredRoute(result);
    if (result) {
      calculateRoute(result.branch); // Assuming `branch` is the destination name
    }
  };

  // Function to calculate route using Google Maps Directions API
  const calculateRoute = (destination) => {
    if (currentPosition && destination) {
      const service = new window.google.maps.DirectionsService();
      service.route(
        {
          origin: currentPosition,
          destination: destination,
          travelMode: window.google.maps.TravelMode.DRIVING,
        },
        (result, status) => {
          if (status === window.google.maps.DirectionsStatus.OK) {
            setDirectionsResponse(result);
          } else {
            console.error(`error fetching directions ${result}`);
          }
        }
      );
    }
  };

  // Render star emojis based on the rating
  const renderStars = () => {
    const totalStars = 5;
    const stars = [];
    for (let i = 1; i <= totalStars; i++) {
      stars.push(
        <span
          key={i}
          className="star"
          style={{cursor: 'pointer' , color: i <= (hoverRating || rating) ? '#ffc107' : '#ffc' }}
          onMouseEnter={() => setHoverRating(i)}
        >
          â˜…
        </span>
      );
    }
    return stars;
  };

  return (
    <div className="supriya">
      <div className="su">
        <h1 style={{ textAlign: 'center', color: 'black', textDecoration: 'underline' }}>
          Enter Your Village Name
        </h1>
        <form onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Search Route Name"
            value={searchTerm}
            onChange={handleInputChange}
          />
          <br />
          <button type="submit" onClick={()=>{
            let row = Math.floor(Math.random()*6)+1;
            switch (row) {
                case 1:
                         var img = document.getElementById("image-element");
                         img.src = "data:image/jpeg;base64,";
                         img.width =300;
                         img.height =300;
                    break;
                    case 2:
                        var img = document.getElementById("image-element");
                        img.src = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS3y9FjCfodjMBETCyv68KqW8iPMXw4VS--xg&s";
                        img.width =300;
                        img.height =300;
                        break;
                    case 3:
                            var img = document.getElementById("image-element");
                            img.src = "data:image/jpeg;base64,";
                            img.width =300;
                                img.height =300;
                          break;
                    case 4:
                            var img = document.getElementById("image-element");
                            img.src = "data:image/jpeg;base64,";
                            img.width =300;
                                img.height =300;
                            break;
                     case 5:
                            var img = document.getElementById("image-element");
                            img.src = "data:image/jpeg;base64,";
                            img.width =300;
                            img.height =300;
                            break;
                default:
                    break;
            }
          }}>Search</button>
        </form>
        <marquee>I want to include some data and fixing errors don't be afraid data is under construction</marquee>
        {/* Display the first matching route details */}
        <div className="priya">
          {filteredRoute ? (
            <div style={{ margin: '10px 0' }}>
              <div><strong>Route Name:</strong> {filteredRoute.routename}</div>
              {filteredRoute.students && (
                <div><strong>Students:</strong> {filteredRoute.students}</div>
              )}
              {filteredRoute.capacity && (
                <div><strong>Capacity:</strong> {filteredRoute.capacity}</div>
              )}
              {filteredRoute.regno && (
                <div><strong>Bus no:</strong> {filteredRoute.regno}</div>
              )}
              {filteredRoute.distance && (
                <div><strong>Distance:</strong> {filteredRoute.distance}</div>
              )}
              {filteredRoute.kms && (
                <div><strong>Kms:</strong> {filteredRoute.kms}</div>
              )}
              {filteredRoute.branch && (
                <div><strong>Branch:</strong> {filteredRoute.branch}</div>
              )}
              {filteredRoute.society && (
                <div><strong>Starting place:</strong> {filteredRoute.society}</div>
              )}
              <div><strong>Driver Number:</strong> {"987654321"}</div>
              <div><strong>Starting Time:</strong> {"8:15AM"}</div>
              <div><strong>Leaving Time:</strong> {"4:45PM"}</div>

              {/* Display random driver rating */}
              <div>
                <strong>Bus Rating:</strong> {renderStars()}
              </div>
              <img src="" id="image-element" alt="Route" />
            </div>
          ) : (
            <p>No matching route found.</p>
          )}
        {/* {currentPosition && filteredRoute && (
          <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
            <GoogleMap
              center={currentPosition}
              zoom={10}
              mapContainerStyle={{ height: '400px', width: '100%' }}
            >
              {directionsResponse && (
                <DirectionsRenderer directions={directionsResponse} />
              )}
            </GoogleMap>
          </LoadScript> 
        )}*/}
        </div>
      </div>
    </div>
  );
};
export default BusRouteList;